import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import Break from "./Break";
import Focus from "./Focus.js"
import Timer from "./Timer.js"

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [breakTime, setBreakTime] = useState(5)
  const [focusTime, setFocusTime] = useState(25)
  const [countDown, setCountDown] = useState(0)
  const initialTime = focusTime;

  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      if(countDown < focusTime ){
        setCountDown(countDown => countDown + 1)
      }
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    if(!isTimerRunning){
      setIsTimerRunning((prevState) => !prevState)
    }
    if(isTimerRunning){
      setIsTimerRunning((prevState) => !prevState)
    }
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <Focus focusTime={focusTime} setFocusTime={setFocusTime} isTimerRunning={isTimerRunning} setIsTimerRunning={setIsTimerRunning}/>
        </div>
        <div className="col">
          <div className="float-right">
            <Break breakTime={breakTime} setBreakTime={setBreakTime} isTimerRunning={isTimerRunning} setIsTimerRunning={setIsTimerRunning}/>
          </div>
        </div>
      </div>
      <div className="row">

        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>

      </div>
     <Timer focusTime={focusTime} setFocusTime={setFocusTime} breakTime={breakTime} setBreakTime={setBreakTime} isTimerRunning={isTimerRunning} setIsTimerRunning={setIsTimerRunning} 
     countDown={countDown} setCountDown={setCountDown}/>
    </div>
  );
}

export default Pomodoro;
