import React, {useState} from 'react'

export default function Timer({display, focusTime,countUp,breakTime}){


    
    
    return (
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            {countUp < focusTime ? (
              <h2 data-testid="session-title">
                {display ? `Focusing for ${focusTime}` : ""}
              </h2>
            ) : (
              <h2 data-testid="session-title">
                {display ? `On Break for ${breakTime}` : ""}
              </h2>
            )}
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {display ? `${focusTime - countUp}` : ""}
            </p>
            <h2>{!display ? "" : "Paused"}</h2>
          </div>
        </div>
        {countUp < focusTime ? (
          <section>
            {display ? (
              <div className="row mb-2">
                <div className="col">
                  <div className="progress" style={{ height: "20px" }}>
                    <div
                      className="progress-bar"
                      role="progressbar"
                      aria-valuemin="0"
                      aria-valuemax={`${focusTime}`}
                      aria-valuenow={`${countUp}`} // TODO: Increase aria-valuenow as elapsed time increases
                      style={{ width: `${(countUp / focusTime) * 100}%` }} // TODO: Increase width % as elapsed time increases
                    />
                  </div>
                </div>
              </div>
            ) : (
              ""
            )}
          </section>
        ) : (
          <section>
            {display ? (
              <div className="row mb-2">
                <div className="col">
                  <div className="progress" style={{ height: "20px" }}>
                    <div
                      className="progress-bar"
                      role="progressbar"
                      aria-valuemin="0"
                      aria-valuemax={`${breakTime}`}
                      aria-valuenow={`${countUp}`} // TODO: Increase aria-valuenow as elapsed time increases
                      style={{ width: `${(countUp / breakTime) * 100}%` }} // TODO: Increase width % as elapsed time increases
                    />
                  </div>
                </div>
              </div>
            ) : (
              ""
            )}
          </section>
        )}
      </div>
    );
}
